function sendNewMessage(e){$.post({url:"/Chat/AddNewMessage",data:{NewMessageText:$("#newMessageText").val(),NewMessageTopicId:e,NewMessagePictureUrl:$("#imageUrlPicker").val()},success:function(e){console.log(e),"YOU ARE MUTED"===e&&alert("You are muted!"),window.location.reload()}})}function removeMessage(e){$.post({url:"/Chat/RemoveMessage/?id="+e,success:function(e){console.log(e),window.location.reload()}})}function replyToMessage(e){$("#newMessageText").val($("#newMessageText").val()+">>"+e+"\n")}function sendNewTopic(){$.post({url:"/Chat/AddNewTopic",data:{NewMessageText:$("#newTopicText").val(),NewTopicTitle:$("#newTopicTitle").val(),NewMessagePictureUrl:$("#imageUrlPicker").val()},success:function(e){window.location.reload(),console.log(e)}})}function removeTopic(e){$.post({url:"/Chat/RemoveTopic/?id="+e,success:function(e){console.log(e),window.location.reload()}})}function toggleTopicSticked(e){$.post({url:"/Chat/ToggleTopicSticky/?id="+e,type:"POST",success:function(e){console.log(e),window.location.reload()}})}function toggleTopicClosed(e){$.post({url:"/Chat/ToggleTopicClosed/?id="+e,success:function(e){console.log(e),window.location.reload()}})}function toggleSubscribeTopic(e){toggleLike(e,"Topic","Subscription")}function toggleLike(e,o,c){$.post({url:"/Chat/ToggleLike"+o+"/?id="+e+"&type="+c,success:function(e){console.log(e),window.location.reload()}})}function uploadImage(){var e=new FormData($("#uploadForm")[0]);$.ajax({url:"/File/AddFile",type:"POST",data:e,contentType:!1,dataType:!1,processData:!1,success:function(e){$("#imageUrlPicker").val("/uploads/"+e)}})}